    int bNo;
    String cName;
    
    double fPower;
    double lPower;
    double frPower;
    double tvPower;
    
    double fWH;
    double lWH;
    double frWH;
    double tvWH;
    
    int fNo;
    int lNo;
    int frNo;
    int tvNo;
    
    double fUnit;
    double lUnit;
    double frUnit;
    double tvUnit;
    double totalUnit;
    
    double pup;
    double pmb;
    double ap;
    String Q;
    int delayFee;
    
    double bill;
    double finalBill;
    
    public void setValue(){
        String strbNo = txtBillNo.getText();
        bNo = Integer.parseInt(strbNo);
        cName = txtCustomerName.getText();   
    }
    
    public void setUnit(){
        String strFPower = txtFanPower.getText();
        fPower = Double.parseDouble(strFPower);
        String strLPower = txtLightPower.getText();
        lPower = Double.parseDouble(strLPower);
        String strFRPower = txtFridgePower.getText();
        frPower = Double.parseDouble(strFRPower);
        String strTVPower = txtTVPower.getText();
        tvPower = Double.parseDouble(strTVPower);
        
        String strFWH = txtFanWorkingHours.getText();
        fWH = Double.parseDouble(strFWH);
        String strLWH = txtLightWorkingHours.getText();
        lWH = Double.parseDouble(strLWH);
        String strFRWH = txtFridgeWorkingHours.getText();
        frWH = Double.parseDouble(strFRWH);
        String strTVWH = txtTVWorkingHours.getText();
        tvWH = Double.parseDouble(strTVWH);
        
        String strFNo = txtFanNumber.getText();
        fNo = Integer.parseInt(strFNo);
        String strLNo = txtLightNumber.getText();
        lNo = Integer.parseInt(strLNo);
        String strFRNo = txtFridgeNumber.getText();
        frNo = Integer.parseInt(strFRNo);
        String strTVNo = txtTVNumber.getText();
        tvNo = Integer.parseInt(strTVNo);
        
    }
   
    public void unitCalculation(){
        setUnit();
        fUnit = (fPower*fWH*fNo)/1000;
        lUnit = (lPower*lWH*lNo)/1000;
        frUnit = (frPower*frWH*frNo)/1000;
        tvUnit = (tvPower*tvWH*tvNo)/1000;  
        totalUnit = fUnit+lUnit+frUnit+tvUnit;
    }
    
    public void billCalculation(){
        unitCalculation();
        String strPUP = txtPerUnitPrice.getText();
        pup = Double.parseDouble(strPUP);
        bill = (double)totalUnit*pup;
    }
    public void finalBillCalculation(){
        billCalculation();
        String strPMB = txtPreviousMonthBill.getText();
        pmb = Double.parseDouble(strPMB);
        String strAP = txtAdvancedPayment.getText();
        ap = Double.parseDouble(strAP);
        Q = txtYorN.getText();
        if(Q.equalsIgnoreCase("no")){
            delayFee = 500;
        }
        else{
            delayFee = 0;
        }
        finalBill = (bill+pmb+delayFee)-ap;
    }
    
