    private void btUnitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btUnitActionPerformed
        unitCalculation();
        txtFanUnit.setText(String.valueOf(fUnit));
        txtLightUnit.setText(String.valueOf(lUnit));
        txtFridgeUnit.setText(String.valueOf(frUnit));
        txtTVUnit.setText(String.valueOf(tvUnit));
    }//GEN-LAST:event_btUnitActionPerformed

